import { useEffect,useState } from "react";
import { Link } from "react-router-dom";
import { getAllCats, deleteCatById } from "../services/internalApiService";

const AllCats = (props) =>{
    const [cats, setCats] = useState([]);

    useEffect(() => {
        getAllCats()
            .then(data => {
                console.log(data.results)
                setCats(data.results)
            })
            .catch(error =>[
                console.log(error)
            ])
    },[])

    const handleDelete = (idToDelete) => {
        deleteCatById(idToDelete)
            .then(data => {
                // There's no reason to stay on the page after the one item being viewed
                // has been deleted.
                console.log("delete and yeet")
                const filteredCats = cats.filter((cat) => {
                    return cat._id !== idToDelete
            })
            setCats(filteredCats)
            })
            .catch(err => {
                console.log(err)
            })
        }

    // console.log(cats)

    return (
        <div className="body mx-auto text-center">
        <h2>Dashboard</h2>

        {cats.map((cat) => {
            const { _id, name, type, description, skillOne, skillTwo, skillThree } = cat;
            return (
                <div key={_id} className="shadow mb-4 rounded border p-4">
                    <table class="table">
                    <th scope="col">Cat</th>
                    <th scope="col">Type</th>
                    <th scope="col"> Action </th>
                        <tr>
                            <td >
                                <h3>{name}</h3>
                            </td>
                            <td>
                                <p>{type}</p>
                            </td>
                            <td>
                                <Link to={`/cats/${_id}/edit`} className="btn btn-sm btn-outline-warning mx-1">
                                    EDIT 
                                </Link>
                                <Link to={`/cats/${_id}`} className="btn btn-sm btn-outline-success mx-1">
                                    View
                                </Link>
                            </td>
                        </tr>
                    </table>

                </div>
                ) } )
        }
        </div>
    )

}


export default AllCats;
